import React, { useCallback, useState } from 'react';
import { Dimensions, Image, Text, View, VirtualizedList } from 'react-native';
import Animated, {
  interpolate,
  runOnJS,
  runOnUI,
  useAnimatedScrollHandler,
  useAnimatedStyle,
  useDerivedValue,
  useSharedValue,
  withSpring,
} from 'react-native-reanimated';
import Item from './Item';

export type User = {
  id: number;
  username: string;
  avatar: string;
};

type UserScrollProps = {
  users: Array<User>;
};

const { width, height } = Dimensions.get('window');
const PADDING_LEFT_FIRST_ITEM = 70;
const PADDING_ITEM = 25;
export const USER_ITEM_WIDTH = width - PADDING_LEFT_FIRST_ITEM * 2;
const SNAP_INTERVAL = USER_ITEM_WIDTH + PADDING_ITEM;

const AnimatedVirtualList = Animated.createAnimatedComponent(VirtualizedList);

export default ({
  users = [
    {
      id: 1,
      username: 'Kurt Schneider',
      avatar:
        'data:image/jpeg;base64,',
    },
    {
      id: 2,
      username: 'Mark Zukerberg',
      avatar:
        'data:image/jpeg;base64,',
    },
    {
      id: 3,
      username: 'Cristina Contanza',
      avatar:
        'data:image/jpeg;base64,',
    },
    {
      id: 4,
      username: 'Roger Federer',
      avatar:
        'data:image/jpeg;base64,',
    },
  ],
}: UserScrollProps) => {
  const scrollX = useSharedValue(0);
  const [currentPos, setCurrentPos] = useState(0);

  const position = useDerivedValue(() => {
    return scrollX.value / SNAP_INTERVAL;
  });

  const scrollHandler = useAnimatedScrollHandler({
    onScroll: event => {
      scrollX.value = event.contentOffset.x;
      if (scrollX.value / SNAP_INTERVAL > currentPos + 0.9) {
        runOnJS(setCurrentPos)(currentPos + 1);
      } else if (scrollX.value / SNAP_INTERVAL < currentPos - 0.9) {
        runOnJS(setCurrentPos)(currentPos - 1);
      }
    },
  });

  const Name = useCallback(() => {
    return (
      <Animated.Text
        style={[{ fontSize: 16, alignSelf: 'center', fontWeight: 'bold' }]}>
        {users[currentPos].username}
      </Animated.Text>
    );
  }, [currentPos]);

  const renderItem = useCallback(({ item, index }) => {
    return <Item item={item} index={index} users={users} position={position} />;
  }, []);

  return (
    <View style={{ flex: 1 }}>
      <AnimatedVirtualList
        style={{ marginTop: 100, marginBottom: 50, flexGrow: 0 }}
        scrollEventThrottle={1}
        snapToAlignment={'start'}
        snapToInterval={SNAP_INTERVAL}
        renderItem={renderItem}
        data={users}
        showsHorizontalScrollIndicator={false}
        onScroll={scrollHandler}
        overScrollMode={'never'}
        disableIntervalMomentum={true}
        horizontal
        getItem={(data, index) => data[index]}
        getItemCount={data => data.length}
        decelerationRate={0.5}
      />
      <Name />
    </View>
  );
};
