import React, { useCallback, useEffect, useRef, useState } from 'react';
import {
  ActivityIndicator,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  View,
  Image,
  useWindowDimensions,
} from 'react-native';
import Animated, {
  Easing,
  Extrapolate,
  interpolate,
  interpolateColor,
  runOnJS,
  useAnimatedGestureHandler,
  useAnimatedStyle,
  useDerivedValue,
  useSharedValue,
  withSpring,
  withTiming,
} from 'react-native-reanimated';
import { mix, useTiming } from 'react-native-redash';
import { EdgeInsets, useSafeAreaInsets } from 'react-native-safe-area-context';
import Icon from 'react-native-vector-icons/EvilIcons';
import PostItem from '../drag_image/PostItem';
import { HEIGHT_IMAGE } from '../drag_image/PostItem';
import Background from './Background';

export type Post = {
  post_id: number;
  username: string;
  post_image: string;
  avatar: string;
  description: string;
};

interface ListPostProps {
  posts: Array<Post>;
}

export default function ListPost({
  posts = [
    {
      post_id: 1,
      username: 'Kurt Schneider',
      avatar:
        'data:image/jpeg;base64,',
      post_image:
        'https://bloximages.newyork1.vip.townnews.com/outtherecolorado.com/content/tncms/assets/v3/editorial/f/7a/f7a605a2-64d6-11eb-9680-436517a94fcf/601876d7983f0.image.jpg?crop=837%2C837%2C208%2C0&resize=1200%2C1200&order=crop%2Cresize',
      description: 'A beautiful wild world!',
    },
    {
      post_id: 2,
      username: 'Mark Zukerberg',
      avatar:
        'data:image/jpeg;base64,',
      post_image:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgfT4LpvYFQz4LbgWLit3dX5nIUVuqaC5YZTTBUAI9QHm3NR3xvn99gvqaMTtf54LLLo8&usqp=CAU',
      description: 'Be happy!!',
    },
    {
      post_id: 3,
      username: 'Cristina Contanza',
      avatar:
        'data:image/jpeg;base64,',
      post_image:
        'data:image/jpeg;base64,',
      description: "Let's get it.",
    },
  ],
}: ListPostProps) {
  const { width, height } = useWindowDimensions();

  const translateXY: Animated.SharedValue<number> = useSharedValue(0);
  const startingPosition: number = 0;
  const opacity: Animated.SharedValue<number> = useSharedValue(0);
  const x: Animated.SharedValue<number> = useSharedValue(startingPosition);
  const y: Animated.SharedValue<number> = useSharedValue(startingPosition);
  const insets: EdgeInsets = useSafeAreaInsets();
  const offsetY: number = (height - HEIGHT_IMAGE) / 2 - 90 - insets.top;
  const [enable, setEnable] = useState(false);
  const [postSelected, setPostSelected] = useState(posts[0]);

  const [y1, setY1] = useState(0);

  const close = useCallback(() => {
    translateXY.value = 0;
    opacity.value = 0;
    setEnable(false);
  }, []);

  const updateEnable = (enable: boolean, index?: number) => {
    setEnable(enable);
    if (index) setPostSelected(posts[index]);
  };

  const callback = (enable: boolean, index?: number) => {
    'worklet';
    runOnJS(updateEnable)(enable, index);
  };

  const onImagePress = useCallback(
    index => {
      if (!enable) {
        translateXY.value = withSpring(
          1,
          {
            damping: 10,
            stiffness: 80,
          },
          () => callback(true, index),
        );
        opacity.value = withTiming(1);
      }
    },
    [enable],
  );

  const opacityStyles = useAnimatedStyle(
    () => ({
      opacity: translateXY.value,
      backgroundColor: interpolateColor(
        y.value,
        [0, -height / 2, startingPosition, height / 2],
        ['black', 'transparent', 'black', 'transparent'],
      ),
      // zIndex: translateXY.value,
    }),
    [translateXY.value],
  );

  const closeStyles1 = useAnimatedStyle(
    () => ({
      opacity: opacity.value,
      // zIndex: translateXY.value,
    }),
    [translateXY.value, opacity.value],
  );

  const closeStyles2 = useAnimatedStyle(
    () => ({
      opacity: opacity.value,
      // zIndex: translateXY.value,
    }),
    [translateXY.value, opacity.value],
  );

  const panGestureHandler = useAnimatedGestureHandler({
    onStart: (event, ctx) => {},
    onActive: (event, ctx) => {
      x.value = startingPosition + event.translationX;
      y.value = startingPosition + event.translationY;
      opacity.value = withTiming(0, { duration: 200 });
    },
    onEnd: (event, ctx) => {
      if (y.value < -150 || y.value > 150) {
        translateXY.value = withTiming(0, {}, () => callback(false));
        opacity.value = withTiming(0);
        y.value = withTiming(startingPosition);
        x.value = withTiming(startingPosition);
      } else {
        opacity.value = withTiming(1, {
          duration: 500,
          easing: Easing.in(Easing.linear),
        });
        x.value = withTiming(startingPosition, { duration: 300 });
        y.value = withTiming(startingPosition, { duration: 300 });
      }
    },
  });

  const animatedStyle = useAnimatedStyle(() => {
    return {
      transform: [
        {
          translateX: x.value,
        },
        {
          translateY: y.value,
        },
      ],
    };
  });

  mix

  return (
    <SafeAreaView style={{ flex: 1 }}>
      <Animated.ScrollView scrollEventThrottle={1}>
        {posts.map((post, index) => {
          return (
            <View key={post.post_id} onLayout={e => (setY1(e.nativeEvent.layout.y))}>
              <PostItem
                y={y1}
                
                post={post}
                index={index}
                panGestureHandler={panGestureHandler}
                // onImagePress={onImagePress}
                animatedStyle={animatedStyle}
                // translateXY={translateXY}
              />
            </View>
          );
        })}
        <Background
          post={postSelected}
          close={close}
          opacityStyles={opacityStyles}
          closeStyles1={closeStyles1}
          closeStyles2={closeStyles2}
        />
      </Animated.ScrollView>
    </SafeAreaView>
  );
}
